import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

const PresetModal = ({ onClose, onSave, editingPreset, groups, clients }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    base_command: '',
    target_group_id: null,
    commands: {}
  });

  const [selectedGroupClients, setSelectedGroupClients] = useState([]);

  useEffect(() => {
    const fetchPresetDetails = async (presetId) => {
      try {
        const response = await axios.get(`${API_BASE_URL}/presets/${presetId}`);
        const preset = response.data;
        setFormData({
          name: preset.name,
          description: preset.description || '',
          base_command: preset.base_command || '',
          target_group_id: preset.target_group_id,
          commands: preset.commands || {}
        });
      } catch (error) {
        console.error('프리셋 상세 정보 조회 실패:', error);
      }
    };

    if (editingPreset) {
      fetchPresetDetails(editingPreset.id);
    }
  }, [editingPreset]);

  useEffect(() => {
    if (formData.target_group_id) {
      const group = groups.find(g => g.id === formData.target_group_id);
      if (group && group.client_ids) {
        const groupClients = clients.filter(c => group.client_ids.includes(c.id));
        setSelectedGroupClients(groupClients);
      } else {
        setSelectedGroupClients([]);
      }
    } else {
      setSelectedGroupClients([]);
    }
  }, [formData.target_group_id, groups, clients]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      ...formData,
      id: editingPreset ? editingPreset.id : undefined,
    });
    onClose();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCommandChange = (clientId, command) => {
    setFormData(prev => ({
      ...prev,
      commands: {
        ...prev.commands,
        [clientId]: command
      }
    }));
  };

  const handleGroupChange = (groupId) => {
    setFormData(prev => ({
      ...prev,
      target_group_id: groupId,
      commands: {} // 그룹이 변경되면 명령어 초기화
    }));
  };

  const handleBaseCommandChange = (e) => {
    const { value } = e.target;
    setFormData(prev => ({
      ...prev,
      base_command: value
    }));
    
    // 기본 명령어가 변경되면 모든 클라이언트의 명령어를 업데이트
    if (selectedGroupClients.length > 0) {
      const updatedCommands = {};
      selectedGroupClients.forEach(client => {
        updatedCommands[client.id] = value;
      });
      setFormData(prev => ({
        ...prev,
        commands: updatedCommands
      }));
    }
  };

  return (
    <div className="modal" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h3>{editingPreset ? '프리셋 편집' : '새 프리셋 만들기'}</h3>
          <span className="close" onClick={onClose}>&times;</span>
        </div>
        
        <div className="modal-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="presetName">프리셋 이름</label>
              <input 
                type="text" 
                id="presetName"
                name="name"
                className="form-input" 
                placeholder="예: 메인 콘텐츠" 
                value={formData.name}
                onChange={handleChange}
                required 
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="presetDescription">설명 (선택)</label>
              <textarea 
                id="presetDescription"
                name="description"
                className="form-input" 
                rows="2" 
                placeholder="프리셋에 대한 설명을 입력하세요"
                value={formData.description}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="base_command">공통 실행 명령어 (경로 등)</label>
              <textarea 
                id="base_command"
                name="base_command"
                className="form-input" 
                rows="3" 
                placeholder="예: D:\\UnrealProjects\\MyProject\\Windows\\MyProject.exe -messaging -dc_cluster -nosplash -dc_cfg=&quot;C:\\path\\to\\config.ndisplay&quot;"
                value={formData.base_command || ''}
                onChange={handleBaseCommandChange}
              />
              <small className="form-help">기본 명령어를 입력하면 각 클라이언트별 입력창에 자동으로 복사됩니다. (선택 사항)</small>
            </div>

            <div className="form-group">
              <label>대상 그룹 선택</label>
              <div className="radio-group">
                {groups.length === 0 ? (
                  <div className="empty-list-message">
                    먼저 그룹을 생성해주세요
                  </div>
                ) : (
                  groups.map(group => (
                    <label key={group.id} className="radio-label">
                      <input 
                        type="radio"
                        name="target_group_id"
                        checked={formData.target_group_id === group.id}
                        onChange={() => handleGroupChange(group.id)}
                      />
                      <span>{group.name} ({(group.client_ids || []).length}개 클라이언트)</span>
                    </label>
                  ))
                )}
              </div>
            </div>

            {selectedGroupClients.length > 0 && (
              <div className="form-group">
                <label>클라이언트별 추가 파라미터</label>
                <small className="form-help" style={{marginBottom: '10px'}}>공통 명령어 뒤에 추가될 파라미터를 클라이언트별로 입력하세요. (선택 사항)</small>
                <div className="client-command-list">
                  {selectedGroupClients.map(client => (
                    <div key={client.id} className="client-command-item">
                      <label htmlFor={`command-${client.id}`}>{client.name} ({client.ip_address})</label>
                      <input
                        type="text"
                        id={`command-${client.id}`}
                        className="form-input"
                        placeholder={`예: -dc_node=Node_1`}
                        value={formData.commands[client.id] || ''}
                        onChange={(e) => handleCommandChange(client.id, e.target.value)}
                      />
                    </div>
                  ))}
                </div>
              </div>
            )}
            
            <div className="modal-actions">
              <button type="button" className="btn btn-secondary" onClick={onClose}>취소</button>
              <button type="submit" className="btn btn-primary">저장</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PresetModal;
