import React from 'react';

const ClientMonitor = ({ 
  clients, 
  onShowAddClientModal, 
  onShowClientDetail 
}) => {
  const getStatusIcon = (status) => {
    switch(status) {
      case 'online': return 'üü°';
      case 'running': return 'üü¢';
      case 'offline': return 'üî¥';
      default: return 'üî¥';
    }
  };

  const getStatusText = (status) => {
    switch(status) {
      case 'online': return 'ÎåÄÍ∏∞ Ï§ë';
      case 'running': return 'Ïñ∏Î¶¨ÏñºÏóîÏßÑ Ïã§Ìñâ Ï§ë';
      case 'offline': return 'Ïò§ÌîÑÎùºÏù∏';
      default: return 'Ïò§ÌîÑÎùºÏù∏';
    }
  };

  // clientsÍ∞Ä undefinedÏù∏ Í≤ΩÏö∞ Îπà Î∞∞Ïó¥Î°ú Ï≤òÎ¶¨
  const safeClients = clients || [];

  return (
    <div className="client-monitor">
      <div className="monitor-header">
        <h2 className="section-title">
          ÎîîÏä§ÌîåÎ†àÏù¥ ÏÑúÎ≤Ñ Î™®ÎãàÌÑ∞ÎßÅ
          <span style={{ fontSize: '12px', color: 'var(--text-muted)', fontWeight: 'normal' }}>
            ÏûêÎèô ÏÉàÎ°úÍ≥†Ïπ®: <span>30Ï¥à</span>
          </span>
        </h2>
        <button className="btn btn-secondary btn-with-text" onClick={onShowAddClientModal}>
          ‚ûï ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Ï∂îÍ∞Ä
        </button>
      </div>

      <div className="client-grid">
        {safeClients.length === 0 ? (
          <div style={{ gridColumn: '1 / -1', textAlign: 'center', padding: '40px', color: 'var(--text-muted)' }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>üñ•Ô∏è</div>
            <div style={{ fontSize: '14px', marginBottom: '8px' }}>Ïó∞Í≤∞Îêú ÎîîÏä§ÌîåÎ†àÏù¥ ÏÑúÎ≤ÑÍ∞Ä ÏóÜÏäµÎãàÎã§</div>
            <div style={{ fontSize: '12px' }}>ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Ï∂îÍ∞Ä Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌïòÍ±∞ÎÇò Python ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Í∞Ä Ïó∞Í≤∞ÎêòÎ©¥ ÏûêÎèôÏúºÎ°ú ÎÇòÌÉÄÎÇ©ÎãàÎã§</div>
          </div>
        ) : (
          safeClients.map(client => (
            <div 
              key={client.id} 
              className={`client-item ${client.status}`}
              onClick={() => onShowClientDetail(client)}
            >
              <div className="client-icon">{getStatusIcon(client.status)}</div>
              <div className="client-name">{client.name}</div>
              <div className="client-ip">{client.ip_address}</div>
              <div className="client-status">{getStatusText(client.status)}</div>
              {client.status === 'running' && (
                <div className="client-metrics">
                  CPU: {Math.floor(Math.random() * 100)}%<br/>
                  RAM: {Math.floor(Math.random() * 100)}%<br/>
                  ÏßÄÏó∞: {Math.floor(Math.random() * 50) + 10}ms
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ClientMonitor;
