import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

const PresetModal = ({ onClose, onSave, editingPreset, groups, clients }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    group_id: null,
    client_commands: {}
  });

  const [selectedGroup, setSelectedGroup] = useState(null);
  const [isGroupListOpen, setIsGroupListOpen] = useState(false);

  useEffect(() => {
    const fetchPresetDetails = async (presetId) => {
      try {
        const response = await axios.get(`${API_BASE_URL}/presets/${presetId}`);
        const preset = response.data;
        setFormData({
          name: preset.name,
          description: preset.description || '',
          group_id: preset.group_id,
          client_commands: preset.client_commands || {}
        });
        
        // 선택된 그룹 설정
        if (preset.group_id) {
          const group = groups.find(g => g.id === preset.group_id);
          setSelectedGroup(group);
        }
      } catch (error) {
        console.error('프리셋 상세 정보 조회 실패:', error);
      }
    };

    if (editingPreset) {
      fetchPresetDetails(editingPreset.id);
    }
  }, [editingPreset, groups]);

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // 그룹 선택 확인
    if (!formData.group_id) {
      alert('대상 그룹을 선택해주세요.');
      return;
    }
    
    // 클라이언트별 명령어 확인
    const group = groups.find(g => g.id === formData.group_id);
    if (group) {
      const hasEmptyCommand = group.client_ids.some(clientId => {
        const command = formData.client_commands[clientId];
        return !command || command.trim() === '';
      });
      
      if (hasEmptyCommand) {
        alert('모든 클라이언트의 명령어를 입력해주세요.');
        return;
      }
    }
    
    onSave({
      ...formData,
      id: editingPreset ? editingPreset.id : undefined,
    });
    onClose();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGroupSelect = (groupId) => {
    setFormData(prev => ({
      ...prev,
      group_id: groupId,
      client_commands: {} // 그룹이 변경되면 명령어 초기화
    }));
    
    const group = groups.find(g => g.id === groupId);
    setSelectedGroup(group);
    setIsGroupListOpen(false); // 드롭다운 닫기
  };

  const handleCommandChange = (clientId, command) => {
    setFormData(prev => ({
      ...prev,
      client_commands: {
        ...prev.client_commands,
        [clientId]: command
      }
    }));
  };

  return (
    <div className="modal" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h3 id="presetModalTitle">
            {editingPreset ? '📋 프리셋 편집' : '📋 새 프리셋 만들기'}
          </h3>
          <span className="close" onClick={onClose}>&times;</span>
        </div>
        
        <div className="modal-body">
          <form id="presetForm" onSubmit={handleSubmit}>
            {/* 1단계: 기본 정보 */}
            <div className="form-group">
              <label htmlFor="presetName">📋 프리셋 이름</label>
              <input 
                type="text" 
                id="presetName"
                name="name"
                className="form-input" 
                placeholder="예: 메인 콘텐츠" 
                value={formData.name}
                onChange={handleChange}
                required 
              />
              <small className="form-help">알아보기 쉬운 프리셋 이름</small>
            </div>
            
            <div className="form-group">
              <label htmlFor="presetDescription">설명 (선택)</label>
              <textarea 
                id="presetDescription"
                name="description"
                className="form-input" 
                rows="2" 
                placeholder="프리셋에 대한 설명을 입력하세요"
                value={formData.description}
                onChange={handleChange}
              />
            </div>
            
            {/* 2단계: 대상 그룹 선택 (커스텀 드롭다운) */}
            <div className="form-group">
              <label>🎯 대상 그룹 선택 (필수)</label>
              <div className="custom-dropdown">
                <div 
                  className="dropdown-selected" 
                  onClick={() => setIsGroupListOpen(!isGroupListOpen)}
                >
                  {selectedGroup ? (
                    <span>
                      <strong>{selectedGroup.name}</strong>
                      <small> ({selectedGroup.client_ids.length}개 클라이언트)</small>
                    </span>
                  ) : (
                    <span className="placeholder">그룹을 선택하세요...</span>
                  )}
                  <span className={`arrow ${isGroupListOpen ? 'up' : 'down'}`}></span>
                </div>
                {isGroupListOpen && (
                  <div className="dropdown-list">
                    {groups.length === 0 ? (
                      <div className="dropdown-item disabled">
                        먼저 그룹을 생성해주세요
                      </div>
                    ) : (
                      groups.map(group => (
                        <div 
                          key={group.id} 
                          className="dropdown-item"
                          onClick={() => handleGroupSelect(group.id)}
                        >
                          <strong>{group.name}</strong>
                          <small> ({group.client_ids.length}개 클라이언트)</small>
                        </div>
                      ))
                    )}
                  </div>
                )}
              </div>
              <small className="form-help">📌 그룹을 선택하면 해당 클라이언트별로 명령어를 설정할 수 있습니다</small>
            </div>
            
            {/* 3단계: 선택된 그룹의 클라이언트별 명령어 설정 */}
            {selectedGroup && (
              <div className="client-command-section" id="clientCommandSection">
                <div className="client-command-header">
                  <h4>🖥️ 클라이언트별 언리얼엔진 실행 명령어</h4>
                  <span id="selectedGroupName" style={{color: 'var(--text-muted)', fontSize: '12px'}}>
                    선택된 그룹: {selectedGroup.name}
                  </span>
                </div>
                <div id="clientCommandsList">
                  {selectedGroup.client_ids.map(clientId => {
                    const client = clients.find(c => c.id === clientId);
                    if (!client) return null;
                    
                    return (
                      <div key={clientId} className="client-command-item">
                        <label htmlFor={`command_${clientId}`}>
                          🖥️ {client.name} ({client.ip_address})
                        </label>
                        <input 
                          type="text" 
                          id={`command_${clientId}`}
                          data-client-id={clientId}
                          className="form-input" 
                          placeholder={`예: D:\\UnrealProjects\\MyProject\\Windows\\MyProject.exe -dc_node=Node_${selectedGroup.client_ids.indexOf(clientId)} -fullscreen`}
                          value={formData.client_commands[clientId] || ''}
                          onChange={(e) => handleCommandChange(clientId, e.target.value)}
                          required
                        />
                        <small style={{color: 'var(--text-muted)', fontSize: '10px', display: 'block', marginTop: '4px'}}>
                          💡 이 클라이언트에서 실행될 완전한 명령어를 입력하세요
                        </small>
                      </div>
                    );
                  })}
                </div>
                <small className="form-help">
                  💡 <strong>팁:</strong> 각 클라이언트별로 다른 nDisplay 노드를 설정하세요<br/>
                  예시: <code>D:\UnrealProjects\MyProject\Windows\MyProject.exe -dc_node=Node_0 -fullscreen</code>
                </small>
              </div>
            )}
            
            <div className="modal-actions">
              <button type="button" className="btn btn-secondary" onClick={onClose}>취소</button>
              <button type="submit" className="btn btn-primary">저장</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PresetModal;
