import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './App.css';

// API 기본 URL
const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  // 상태 관리
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [clients, setClients] = useState([]);
  const [groups, setGroups] = useState([]);
  const [presets, setPresets] = useState([]);
  const [stats, setStats] = useState({
    totalClients: 0,
    onlineClients: 0,
    totalGroups: 0,
    totalPresets: 0,
    activeConnections: 0
  });

  // 모달 상태
  const [showClientModal, setShowClientModal] = useState(false);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [showPresetModal, setShowPresetModal] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  // 폼 상태
  const [newClient, setNewClient] = useState({ name: '', ip_address: '', port: 8081 });
  const [newGroup, setNewGroup] = useState({ name: '' });
  const [newPreset, setNewPreset] = useState({ name: '', command: '', target_group_id: '' });

  // Socket.io 연결
  useEffect(() => {
    const newSocket = io('http://localhost:8000');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('✅ Socket.io 연결됨');
      setIsConnected(true);
    });

    newSocket.on('disconnect', () => {
      console.log('❌ Socket.io 연결 해제됨');
      setIsConnected(false);
    });

    newSocket.on('client_status_changed', (data) => {
      console.log('클라이언트 상태 변경:', data);
      fetchClients(); // 클라이언트 목록 새로고침
    });

    return () => newSocket.close();
  }, []);

  // 초기 데이터 로드
  useEffect(() => {
    fetchClients();
    fetchGroups();
    fetchPresets();
  }, []);

  // API 함수들
  const fetchClients = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/clients`);
      setClients(response.data);
      updateStats();
    } catch (error) {
      console.error('클라이언트 조회 실패:', error);
    }
  };

  const fetchGroups = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/groups`);
      setGroups(response.data);
      updateStats();
    } catch (error) {
      console.error('그룹 조회 실패:', error);
    }
  };

  const fetchPresets = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/presets`);
      setPresets(response.data);
      updateStats();
    } catch (error) {
      console.error('프리셋 조회 실패:', error);
    }
  };

  const addClient = async () => {
    try {
      await axios.post(`${API_BASE_URL}/clients`, newClient);
      setNewClient({ name: '', ip_address: '', port: 8081 });
      setShowClientModal(false);
      fetchClients();
    } catch (error) {
      console.error('클라이언트 추가 실패:', error);
      alert('클라이언트 추가 실패: ' + error.response?.data?.error || error.message);
    }
  };

  const addGroup = async () => {
    try {
      await axios.post(`${API_BASE_URL}/groups`, newGroup);
      setNewGroup({ name: '' });
      setShowGroupModal(false);
      fetchGroups();
    } catch (error) {
      console.error('그룹 추가 실패:', error);
      alert('그룹 추가 실패: ' + error.response?.data?.error || error.message);
    }
  };

  const addPreset = async () => {
    try {
      const presetData = {
        ...newPreset,
        target_group_id: newPreset.target_group_id || null
      };
      await axios.post(`${API_BASE_URL}/presets`, presetData);
      setNewPreset({ name: '', command: '', target_group_id: '' });
      setShowPresetModal(false);
      fetchPresets();
    } catch (error) {
      console.error('프리셋 추가 실패:', error);
      alert('프리셋 추가 실패: ' + error.response?.data?.error || error.message);
    }
  };

  const executePreset = async (presetId) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/presets/${presetId}/execute`);
      alert(`프리셋 실행됨: ${response.data.preset_name}\n대상 클라이언트: ${response.data.target_clients}개`);
    } catch (error) {
      console.error('프리셋 실행 실패:', error);
      alert('프리셋 실행 실패: ' + error.response?.data?.error || error.message);
    }
  };

  const deleteClient = async (clientId) => {
    if (!window.confirm('정말로 이 클라이언트를 삭제하시겠습니까?')) return;

    try {
      await axios.delete(`${API_BASE_URL}/clients/${clientId}`);
      fetchClients();
    } catch (error) {
      console.error('클라이언트 삭제 실패:', error);
      alert('클라이언트 삭제 실패: ' + error.response?.data?.error || error.message);
    }
  };

  const updateStats = () => {
    setStats({
      totalClients: clients.length,
      onlineClients: clients.filter(c => c.status === 'online').length,
      totalGroups: groups.length,
      totalPresets: presets.length,
      activeConnections: isConnected ? 1 : 0
    });
  };

  // 통계 업데이트
  useEffect(() => {
    updateStats();
  }, [clients, groups, presets, isConnected]);

  return (
    <div className="App">
      {/* 헤더 */}
      <header className="header">
        <div className="header-content">
          <h1>
            ⚡ Switchboard Plus v2.0 MVP
            <span className="socket-status">
              <span className={`socket-indicator ${isConnected ? '' : 'disconnected'}`}></span>
              {isConnected ? '연결됨' : '연결 해제됨'}
            </span>
          </h1>
          <div className="status-info">
            <span>서버: localhost:8000</span>
            <span>클라이언트: {stats.onlineClients}/{stats.totalClients}</span>
          </div>
        </div>
      </header>

      <div className="container">
        {/* 통계 바 */}
        <div className="stats-bar">
          <div className="stats-grid">
            <div className="stat-item">
              <div className="stat-value">{stats.totalClients}</div>
              <div className="stat-label">전체 클라이언트</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.onlineClients}</div>
              <div className="stat-label">온라인</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.totalGroups}</div>
              <div className="stat-label">그룹</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.totalPresets}</div>
              <div className="stat-label">프리셋</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.activeConnections}</div>
              <div className="stat-label">활성 연결</div>
            </div>
          </div>
        </div>

        {/* 메인 레이아웃 */}
        <div className="main-layout">
          {/* 클라이언트 관리 */}
          <div className="section">
            <h2 className="section-title">
              클라이언트 관리
              <button className="btn btn-secondary btn-with-text" onClick={() => setShowClientModal(true)}>
                ➕ 새 클라이언트
              </button>
            </h2>
            <div className="client-grid">
              {clients.map(client => (
                <div key={client.id} className={`client-item ${client.status}`}>
                  <div className="client-header">
                    <div className="client-name">{client.name}</div>
                    <div className={`client-status ${client.status}`}>
                      {client.status === 'online' ? '🟢 온라인' : '🔴 오프라인'}
                    </div>
                  </div>
                  <div className="client-info">
                    <div>IP: {client.ip_address}</div>
                    <div>포트: {client.port}</div>
                    {client.last_seen && <div>마지막 접속: {new Date(client.last_seen).toLocaleString()}</div>}
                  </div>
                  <div className="client-actions">
                    <button className="btn btn-danger btn-small" onClick={() => deleteClient(client.id)}>
                      삭제
                    </button>
                  </div>
                </div>
              ))}
              {clients.length === 0 && (
                <div className="empty-state">
                  <p>등록된 클라이언트가 없습니다.</p>
                  <button className="btn btn-primary" onClick={() => setShowClientModal(true)}>
                    첫 번째 클라이언트 추가
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* 그룹 관리 */}
          <div className="section">
            <h2 className="section-title">
              그룹 관리
              <button className="btn btn-secondary btn-with-text" onClick={() => setShowGroupModal(true)}>
                ➕ 새 그룹
              </button>
            </h2>
            <div className="group-list">
              {groups.map(group => (
                <div key={group.id} className="group-item">
                  <div className="group-name">{group.name}</div>
                  <div className="group-info">
                    생성일: {new Date(group.created_at).toLocaleDateString()}
                  </div>
                </div>
              ))}
              {groups.length === 0 && (
                <div className="empty-state">
                  <p>등록된 그룹이 없습니다.</p>
                  <button className="btn btn-primary" onClick={() => setShowGroupModal(true)}>
                    첫 번째 그룹 추가
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* 프리셋 관리 */}
        <div className="section">
          <h2 className="section-title">
            프리셋 관리
            <button className="btn btn-secondary btn-with-text" onClick={() => setShowPresetModal(true)}>
              ➕ 새 프리셋
            </button>
          </h2>
          <div className="preset-grid">
            {presets.map(preset => (
              <div key={preset.id} className="preset-item">
                <div className="preset-header">
                  <div className="preset-name">{preset.name}</div>
                  <div className="preset-group">
                    {preset.group_name ? `그룹: ${preset.group_name}` : '전체 클라이언트'}
                  </div>
                </div>
                <div className="preset-command">{preset.command}</div>
                <div className="preset-actions">
                  <button className="btn btn-primary" onClick={() => executePreset(preset.id)}>
                    ▶️ 실행
                  </button>
                </div>
              </div>
            ))}
            {presets.length === 0 && (
              <div className="empty-state">
                <p>등록된 프리셋이 없습니다.</p>
                <button className="btn btn-primary" onClick={() => setShowPresetModal(true)}>
                  첫 번째 프리셋 추가
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* 클라이언트 추가 모달 */}
      {showClientModal && (
        <div className="modal-overlay" onClick={() => setShowClientModal(false)}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <h3>새 클라이언트 추가</h3>
            <div className="form-group">
              <label>이름:</label>
              <input
                type="text"
                value={newClient.name}
                onChange={e => setNewClient({...newClient, name: e.target.value})}
                placeholder="Display_01"
              />
            </div>
            <div className="form-group">
              <label>IP 주소:</label>
              <input
                type="text"
                value={newClient.ip_address}
                onChange={e => setNewClient({...newClient, ip_address: e.target.value})}
                placeholder="192.168.1.101"
              />
            </div>
            <div className="form-group">
              <label>포트:</label>
              <input
                type="number"
                value={newClient.port}
                onChange={e => setNewClient({...newClient, port: parseInt(e.target.value)})}
                placeholder="8081"
              />
            </div>
            <div className="modal-actions">
              <button className="btn btn-secondary" onClick={() => setShowClientModal(false)}>
                취소
              </button>
              <button className="btn btn-primary" onClick={addClient}>
                추가
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 그룹 추가 모달 */}
      {showGroupModal && (
        <div className="modal-overlay" onClick={() => setShowGroupModal(false)}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <h3>새 그룹 추가</h3>
            <div className="form-group">
              <label>그룹 이름:</label>
              <input
                type="text"
                value={newGroup.name}
                onChange={e => setNewGroup({...newGroup, name: e.target.value})}
                placeholder="Main_Display_Group"
              />
            </div>
            <div className="modal-actions">
              <button className="btn btn-secondary" onClick={() => setShowGroupModal(false)}>
                취소
              </button>
              <button className="btn btn-primary" onClick={addGroup}>
                추가
              </button>
            </div>
          </div>
        </div>
      )}

      {/* 프리셋 추가 모달 */}
      {showPresetModal && (
        <div className="modal-overlay" onClick={() => setShowPresetModal(false)}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <h3>새 프리셋 추가</h3>
            <div className="form-group">
              <label>프리셋 이름:</label>
              <input
                type="text"
                value={newPreset.name}
                onChange={e => setNewPreset({...newPreset, name: e.target.value})}
                placeholder="언리얼엔진 실행"
              />
            </div>
            <div className="form-group">
              <label>명령어:</label>
              <textarea
                value={newPreset.command}
                onChange={e => setNewPreset({...newPreset, command: e.target.value})}
                placeholder="C:\Program Files\Epic Games\UE_5.3\Engine\Binaries\Win64\UnrealEditor.exe -ndisplay"
                rows="3"
              />
            </div>
            <div className="form-group">
              <label>대상 그룹 (선택사항):</label>
              <select
                value={newPreset.target_group_id}
                onChange={e => setNewPreset({...newPreset, target_group_id: e.target.value})}
              >
                <option value="">전체 클라이언트</option>
                {groups.map(group => (
                  <option key={group.id} value={group.id}>{group.name}</option>
                ))}
              </select>
            </div>
            <div className="modal-actions">
              <button className="btn btn-secondary" onClick={() => setShowPresetModal(false)}>
                취소
              </button>
              <button className="btn btn-primary" onClick={addPreset}>
                추가
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
