import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './App.css';

// API ê¸°ë³¸ URL
const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  // ìƒíƒœ ê´€ë¦¬
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [clients, setClients] = useState([]);
  const [groups, setGroups] = useState([]);
  const [presets, setPresets] = useState([]);
  const [stats, setStats] = useState({
    totalClients: 0,
    onlineClients: 0,
    totalGroups: 0,
    totalPresets: 0,
    activeConnections: 0
  });

  // ëª¨ë‹¬ ìƒíƒœ
  const [showClientModal, setShowClientModal] = useState(false);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [showPresetModal, setShowPresetModal] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  // í¼ ìƒíƒœ
  const [newClient, setNewClient] = useState({ name: '', ip_address: '', port: 8081 });
  const [newGroup, setNewGroup] = useState({ name: '' });
  const [newPreset, setNewPreset] = useState({ name: '', command: '', target_group_id: '' });

  // Socket.io ì—°ê²°
  useEffect(() => {
    const newSocket = io('http://localhost:8000');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('âœ… Socket.io ì—°ê²°ë¨');
      setIsConnected(true);
    });

    newSocket.on('disconnect', () => {
      console.log('âŒ Socket.io ì—°ê²° í•´ì œë¨');
      setIsConnected(false);
    });

    newSocket.on('client_status_changed', (data) => {
      console.log('í´ë¼ì´ì–¸íŠ¸ ìƒíƒœ ë³€ê²½:', data);
      fetchClients(); // í´ë¼ì´ì–¸íŠ¸ ëª©ë¡ ìƒˆë¡œê³ ì¹¨
    });

    return () => newSocket.close();
  }, []);

  // ì´ˆê¸° ë°ì´í„° ë¡œë“œ
  useEffect(() => {
    fetchClients();
    fetchGroups();
    fetchPresets();
  }, []);

  // API í•¨ìˆ˜ë“¤
  const fetchClients = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/clients`);
      setClients(response.data);
      updateStats();
    } catch (error) {
      console.error('í´ë¼ì´ì–¸íŠ¸ ì¡°íšŒ ì‹¤íŒ¨:', error);
    }
  };

  const fetchGroups = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/groups`);
      setGroups(response.data);
      updateStats();
    } catch (error) {
      console.error('ê·¸ë£¹ ì¡°íšŒ ì‹¤íŒ¨:', error);
    }
  };

  const fetchPresets = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/presets`);
      setPresets(response.data);
      updateStats();
    } catch (error) {
      console.error('í”„ë¦¬ì…‹ ì¡°íšŒ ì‹¤íŒ¨:', error);
    }
  };

  const addClient = async () => {
    try {
      await axios.post(`${API_BASE_URL}/clients`, newClient);
      setNewClient({ name: '', ip_address: '', port: 8081 });
      setShowClientModal(false);
      fetchClients();
    } catch (error) {
      console.error('í´ë¼ì´ì–¸íŠ¸ ì¶”ê°€ ì‹¤íŒ¨:', error);
      alert('í´ë¼ì´ì–¸íŠ¸ ì¶”ê°€ ì‹¤íŒ¨: ' + error.response?.data?.error || error.message);
    }
  };

  const addGroup = async () => {
    try {
      await axios.post(`${API_BASE_URL}/groups`, newGroup);
      setNewGroup({ name: '' });
      setShowGroupModal(false);
      fetchGroups();
    } catch (error) {
      console.error('ê·¸ë£¹ ì¶”ê°€ ì‹¤íŒ¨:', error);
      alert('ê·¸ë£¹ ì¶”ê°€ ì‹¤íŒ¨: ' + error.response?.data?.error || error.message);
    }
  };

  const addPreset = async () => {
    try {
      const presetData = {
        ...newPreset,
        target_group_id: newPreset.target_group_id || null
      };
      await axios.post(`${API_BASE_URL}/presets`, presetData);
      setNewPreset({ name: '', command: '', target_group_id: '' });
      setShowPresetModal(false);
      fetchPresets();
    } catch (error) {
      console.error('í”„ë¦¬ì…‹ ì¶”ê°€ ì‹¤íŒ¨:', error);
      alert('í”„ë¦¬ì…‹ ì¶”ê°€ ì‹¤íŒ¨: ' + error.response?.data?.error || error.message);
    }
  };

  const executePreset = async (presetId) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/presets/${presetId}/execute`);
      alert(`í”„ë¦¬ì…‹ ì‹¤í–‰ë¨: ${response.data.preset_name}\nëŒ€ìƒ í´ë¼ì´ì–¸íŠ¸: ${response.data.target_clients}ê°œ`);
    } catch (error) {
      console.error('í”„ë¦¬ì…‹ ì‹¤í–‰ ì‹¤íŒ¨:', error);
      alert('í”„ë¦¬ì…‹ ì‹¤í–‰ ì‹¤íŒ¨: ' + error.response?.data?.error || error.message);
    }
  };

  const deleteClient = async (clientId) => {
    if (!window.confirm('ì •ë§ë¡œ ì´ í´ë¼ì´ì–¸íŠ¸ë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?')) return;

    try {
      await axios.delete(`${API_BASE_URL}/clients/${clientId}`);
      fetchClients();
    } catch (error) {
      console.error('í´ë¼ì´ì–¸íŠ¸ ì‚­ì œ ì‹¤íŒ¨:', error);
      alert('í´ë¼ì´ì–¸íŠ¸ ì‚­ì œ ì‹¤íŒ¨: ' + error.response?.data?.error || error.message);
    }
  };

  const updateStats = () => {
    setStats({
      totalClients: clients.length,
      onlineClients: clients.filter(c => c.status === 'online').length,
      totalGroups: groups.length,
      totalPresets: presets.length,
      activeConnections: isConnected ? 1 : 0
    });
  };

  // í†µê³„ ì—…ë°ì´íŠ¸
  useEffect(() => {
    updateStats();
  }, [clients, groups, presets, isConnected]);

  return (
    <div className="App">
      {/* í—¤ë” */}
      <header className="header">
        <div className="header-content">
          <h1>
            âš¡ Switchboard Plus v2.0 MVP
            <span className="socket-status">
              <span className={`socket-indicator ${isConnected ? '' : 'disconnected'}`}></span>
              {isConnected ? 'ì—°ê²°ë¨' : 'ì—°ê²° í•´ì œë¨'}
            </span>
          </h1>
          <div className="status-info">
            <span>ì„œë²„: localhost:8000</span>
            <span>í´ë¼ì´ì–¸íŠ¸: {stats.onlineClients}/{stats.totalClients}</span>
          </div>
        </div>
      </header>

      <div className="container">
        {/* í†µê³„ ë°” */}
        <div className="stats-bar">
          <div className="stats-grid">
            <div className="stat-item">
              <div className="stat-value">{stats.totalClients}</div>
              <div className="stat-label">ì „ì²´ í´ë¼ì´ì–¸íŠ¸</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.onlineClients}</div>
              <div className="stat-label">ì˜¨ë¼ì¸</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.totalGroups}</div>
              <div className="stat-label">ê·¸ë£¹</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.totalPresets}</div>
              <div className="stat-label">í”„ë¦¬ì…‹</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">{stats.activeConnections}</div>
              <div className="stat-label">í™œì„± ì—°ê²°</div>
            </div>
          </div>
        </div>

        {/* ë©”ì¸ ë ˆì´ì•„ì›ƒ */}
        <div className="main-layout">
          {/* í´ë¼ì´ì–¸íŠ¸ ê´€ë¦¬ */}
          <div className="section">
            <h2 className="section-title">
              í´ë¼ì´ì–¸íŠ¸ ê´€ë¦¬
              <button className="btn btn-secondary btn-with-text" onClick={() => setShowClientModal(true)}>
                â• ìƒˆ í´ë¼ì´ì–¸íŠ¸
              </button>
            </h2>
            <div className="client-grid">
              {clients.map(client => (
                <div key={client.id} className={`client-item ${client.status}`}>
                  <div className="client-header">
                    <div className="client-name">{client.name}</div>
                    <div className={`client-status ${client.status}`}>
                      {client.status === 'online' ? 'ğŸŸ¢ ì˜¨ë¼ì¸' : 'ğŸ”´ ì˜¤í”„ë¼ì¸'}
                    </div>
                  </div>
                  <div className="client-info">
                    <div>IP: {client.ip_address}</div>
                    <div>í¬íŠ¸: {client.port}</div>
                    {client.last_seen && <div>ë§ˆì§€ë§‰ ì ‘ì†: {new Date(client.last_seen).toLocaleString()}</div>}
                  </div>
                  <div className="client-actions">
                    <button className="btn btn-danger btn-small" onClick={() => deleteClient(client.id)}>
                      ì‚­ì œ
                    </button>
                  </div>
                </div>
              ))}
              {clients.length === 0 && (
                <div className="empty-state">
                  <p>ë“±ë¡ëœ í´ë¼ì´ì–¸íŠ¸ê°€ ì—†ìŠµë‹ˆë‹¤.</p>
                  <button className="btn btn-primary" onClick={() => setShowClientModal(true)}>
                    ì²« ë²ˆì§¸ í´ë¼ì´ì–¸íŠ¸ ì¶”ê°€
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* ê·¸ë£¹ ê´€ë¦¬ */}
          <div className="section">
            <h2 className="section-title">
              ê·¸ë£¹ ê´€ë¦¬
              <button className="btn btn-secondary btn-with-text" onClick={() => setShowGroupModal(true)}>
                â• ìƒˆ ê·¸ë£¹
