import React from 'react';

const Toast = ({ show, message, type }) => {
  if (!show) return null;
  let toastClass = 'toast';
  if (type) toastClass += ` ${type}`;
  return (
    <div className={toastClass} style={{ display: 'block' }}>
      {message}
    </div>
  );
};

export default Toast;
