import React from 'react';

const GroupSection = ({ 
  groups, 
  clients, 
  selectedGroups, 
  onSelectAllGroups, 
  onSelectGroup, 
  onBulkGroupAction, 
  onShowAddGroupModal, 
  onEditGroup, 
  onDeleteGroup 
}) => {
  const getClientTags = (clientIds) => {
    if (!clients || !Array.isArray(clients)) return [];
    
    return clientIds.map(clientId => {
      const client = clients.find(c => c.id === clientId);
      return client ? (
        <span key={clientId} className="client-tag">{client.ip_address}</span>
      ) : null;
    }).filter(Boolean);
  };

  // groups가 undefined인 경우 빈 배열로 처리
  const safeGroups = groups || [];
  const safeClients = clients || [];

  return (
    <div className="section">
      <h2 className="section-title">
        디스플레이 서버 그룹
        <button className="btn btn-secondary btn-with-text" onClick={onShowAddGroupModal}>
          ➕ 새 그룹
        </button>
      </h2>
      
      {/* 그룹 일괄 제어 UI */}
      <div className="bulk-controls">
        <div className="selection-info">
          <label>
            <input 
              type="checkbox" 
              checked={selectedGroups.size === safeGroups.length && safeGroups.length > 0}
              onChange={onSelectAllGroups}
            />
            전체 선택
          </label>
          <span>선택된 그룹: <span>{selectedGroups.size}</span>개</span>
        </div>
        <div className="bulk-actions">
          <button 
            className="btn btn-primary btn-bulk" 
            onClick={() => onBulkGroupAction('on')} 
            title="선택된 그룹들 전체 켜기"
          >
            🔌
          </button>
          <button 
            className="btn btn-secondary btn-bulk" 
            onClick={() => onBulkGroupAction('reboot')} 
            title="선택된 그룹들 전체 재부팅"
          >
            🔄
          </button>
          <button 
            className="btn btn-danger btn-bulk" 
            onClick={() => onBulkGroupAction('off')} 
            title="선택된 그룹들 전체 끄기"
          >
            ⚡
          </button>
        </div>
      </div>

      <div className="group-grid">
        {safeGroups.length === 0 ? (
          <div style={{ gridColumn: '1 / -1', textAlign: 'center', padding: '40px', color: 'var(--text-muted)' }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>👥</div>
            <div style={{ fontSize: '14px', marginBottom: '8px' }}>아직 생성된 그룹이 없습니다</div>
            <div style={{ fontSize: '12px' }}>새 그룹 버튼을 클릭해서 첫 번째 그룹을 만들어보세요!</div>
          </div>
        ) : (
          safeGroups.map(group => (
            <div key={group.id} className="group-card">
              <input 
                type="checkbox" 
                className="group-checkbox" 
                checked={selectedGroups.has(group.id)}
                onChange={() => onSelectGroup(group.id)}
                onClick={(e) => e.stopPropagation()}
              />
              <div className="group-content">
                <div className="group-name">{group.name}</div>
                <div className="group-info">{(group.client_ids || []).length}개 디스플레이 서버</div>
                <div className="group-clients">
                  {getClientTags(group.client_ids || [])}
                </div>
              </div>
              <div className="group-actions">
                <button 
                  className="btn btn-secondary" 
                  onClick={() => onEditGroup(group)} 
                  title="편집"
                >
                  ✏️
                </button>
                <button 
                  className="btn btn-danger" 
                  onClick={() => onDeleteGroup(group.id)} 
                  title="삭제"
                >
                  🗑️
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default GroupSection;
