import React from 'react';

const PresetSection = ({ 
  presets, 
  groups, 
  selectedPresets, 
  onSelectAllPresets, 
  onSelectPreset, 
  onBulkPresetAction, 
  onShowAddPresetModal, 
  onExecutePreset, 
  onEditPreset, 
  onDeletePreset 
}) => {
  const getTotalClientsInGroups = (groupIds) => {
    const clientSet = new Set();
    groupIds.forEach(groupId => {
      const group = groups.find(g => g.id === groupId);
      if (group) {
        group.client_ids.forEach(clientId => clientSet.add(clientId));
      }
    });
    return clientSet.size;
  };

  const getGroupNames = (groupIds) => {
    return groupIds.map(groupId => {
      const group = groups.find(g => g.id === groupId);
      return group ? group.name : 'ÏÇ≠Ï†úÎêú Í∑∏Î£π';
    }).join(', ');
  };

  return (
    <div className="section">
      <h2 className="section-title">
        ÏΩòÌÖêÏ∏† ÌîÑÎ¶¨ÏÖã
        <button className="btn btn-secondary btn-with-text" onClick={onShowAddPresetModal}>
          ‚ûï ÏÉà ÌîÑÎ¶¨ÏÖã
        </button>
      </h2>
      
      {/* ÌîÑÎ¶¨ÏÖã ÏùºÍ¥Ñ Ï†úÏñ¥ UI */}
      <div className="bulk-controls">
        <div className="selection-info">
          <label>
            <input 
              type="checkbox" 
              checked={selectedPresets.size === presets.length && presets.length > 0}
              onChange={onSelectAllPresets}
            />
            Ï†ÑÏ≤¥ ÏÑ†ÌÉù
          </label>
          <span>ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖã: <span>{selectedPresets.size}</span>Í∞ú</span>
        </div>
        <div className="bulk-actions">
          <button 
            className="btn btn-primary btn-bulk" 
            onClick={() => onBulkPresetAction('execute')} 
            title="ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖãÎì§ Ï†ÑÏ≤¥ Ïã§Ìñâ"
          >
            ‚ñ∂Ô∏è
          </button>
          <button 
            className="btn btn-danger btn-bulk" 
            onClick={() => onBulkPresetAction('stop')} 
            title="ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖãÎì§ Ï†ÑÏ≤¥ Ï†ïÏßÄ"
          >
            ‚èπÔ∏è
          </button>
          <button 
            className="btn btn-danger btn-bulk" 
            onClick={() => onBulkPresetAction('delete')} 
            title="ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖãÎì§ Ï†ÑÏ≤¥ ÏÇ≠Ï†ú"
          >
            üóëÔ∏è
          </button>
        </div>
      </div>

      <div className="preset-grid">
        {presets.length === 0 ? (
          <div style={{ gridColumn: '1 / -1', textAlign: 'center', padding: '40px', color: 'var(--text-muted)' }}>
            <div style={{ fontSize: '48px', marginBottom: '16px' }}>üìù</div>
            <div style={{ fontSize: '14px', marginBottom: '8px' }}>ÏïÑÏßÅ ÏÉùÏÑ±Îêú ÌîÑÎ¶¨ÏÖãÏù¥ ÏóÜÏäµÎãàÎã§</div>
            <div style={{ fontSize: '12px' }}>ÏÉà ÌîÑÎ¶¨ÏÖã Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠Ìï¥ÏÑú Ï≤´ Î≤àÏß∏ ÌîÑÎ¶¨ÏÖãÏùÑ ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî!</div>
          </div>
        ) : (
          presets.map(preset => (
            <div key={preset.id} className={`preset-card ${preset.is_active ? 'active' : ''}`}>
              <input 
                type="checkbox" 
                className="preset-checkbox" 
                checked={selectedPresets.has(preset.id)}
                onChange={() => onSelectPreset(preset.id)}
                onClick={(e) => e.stopPropagation()}
              />
              <div className="preset-content">
                <div className="preset-name">{preset.name}</div>
                {preset.description && <div className="preset-info">{preset.description}</div>}
                <div className="preset-info">Í∑∏Î£π: {getGroupNames(preset.group_ids)}</div>
                <div className="preset-info">
                  {preset.group_ids.length}Í∞ú Í∑∏Î£π, {getTotalClientsInGroups(preset.group_ids)}ÎåÄ ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏
                </div>
              </div>
              <div className="preset-actions">
                <button 
                  className="btn btn-primary" 
                  onClick={() => onExecutePreset(preset.id)} 
                  title="Ïã§Ìñâ"
                >
                  ‚ñ∂Ô∏è
                </button>
                <button 
                  className="btn btn-secondary" 
                  onClick={() => onEditPreset(preset)} 
                  title="Ìé∏Ïßë"
                >
                  ‚úèÔ∏è
                </button>
                <button 
                  className="btn btn-danger" 
                  onClick={() => onDeletePreset(preset.id)} 
                  title="ÏÇ≠Ï†ú"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default PresetSection;
