import React, { useState, useEffect } from 'react';

const PresetModal = ({ onClose, onSave, editingPreset, groups, clients }) => {
  console.log('ÌîÑÎ¶¨ÏÖã Î™®Îã¨Ïù¥ Î∞õÏùÄ Í∑∏Î£π Îç∞Ïù¥ÌÑ∞:', groups);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    group_id: null,
    client_commands: {}
  });

  // editingPresetÏù¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßå Ìèº Îç∞Ïù¥ÌÑ∞Î•º ÏÑ§Ï†ïÌï©ÎãàÎã§.
  useEffect(() => {
    if (editingPreset && groups.length > 0) {
      const groupExists = groups.some(g => g.id === editingPreset.group_id);
      setFormData({
        name: editingPreset.name || '',
        description: editingPreset.description || '',
        group_id: groupExists ? editingPreset.group_id : null,
        client_commands: editingPreset.client_commands || {}
      });
    } else {
      // ÏÉà ÌîÑÎ¶¨ÏÖãÏùÑ ÎßåÎì§Í±∞ÎÇò ÏàòÏ†ïÌï† ÌîÑÎ¶¨ÏÖãÏù¥ ÏóÜÏùÑ Í≤ΩÏö∞ Ìèº Ï¥àÍ∏∞Ìôî
      setFormData({
        name: '',
        description: '',
        group_id: null,
        client_commands: {}
      });
    }
  }, [editingPreset, groups]);

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.group_id) {
      alert('ÎåÄÏÉÅ Í∑∏Î£πÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    
    const group = groups.find(g => g.id === formData.group_id);
    if (group) {
      const hasEmptyCommand = (group.client_ids || []).some(clientId => {
        const command = formData.client_commands[clientId];
        return !command || command.trim() === '';
      });
      
      if (hasEmptyCommand) {
        alert('Î™®Îì† ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Ïùò Î™ÖÎ†πÏñ¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        return;
      }
    }
    
    onSave(formData);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleGroupSelect = (groupId) => {
    setFormData(prev => ({
      ...prev,
      group_id: groupId,
      client_commands: {} // Í∑∏Î£π Î≥ÄÍ≤Ω Ïãú Î™ÖÎ†πÏñ¥ Ï¥àÍ∏∞Ìôî
    }));
  };

  const handleCommandChange = (clientId, command) => {
    setFormData(prev => ({
      ...prev,
      client_commands: {
        ...prev.client_commands,
        [clientId]: command
      }
    }));
  };
  
  const selectedGroup = formData.group_id ? groups.find(g => g.id === formData.group_id) : null;

  return (
    <div className="modal" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h3 id="presetModalTitle">
            {editingPreset ? 'üìã ÌîÑÎ¶¨ÏÖã Ìé∏Ïßë' : 'üìã ÏÉà ÌîÑÎ¶¨ÏÖã ÎßåÎì§Í∏∞'}
          </h3>
          <span className="close" onClick={onClose}>&times;</span>
        </div>
        
        <div className="modal-body">
          <form id="presetForm" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="presetName">üìã ÌîÑÎ¶¨ÏÖã Ïù¥Î¶Ñ</label>
              <input 
                type="text" 
                id="presetName"
                name="name"
                className="form-input" 
                placeholder="Ïòà: Î©îÏù∏ ÏΩòÌÖêÏ∏†" 
                value={formData.name}
                onChange={handleChange}
                required 
              />
              <small className="form-help">ÏïåÏïÑÎ≥¥Í∏∞ Ïâ¨Ïö¥ ÌîÑÎ¶¨ÏÖã Ïù¥Î¶Ñ</small>
            </div>
            
            <div className="form-group">
              <label htmlFor="presetDescription">ÏÑ§Î™Ö (ÏÑ†ÌÉù)</label>
              <textarea 
                id="presetDescription"
                name="description"
                className="form-input" 
                rows="2" 
                placeholder="ÌîÑÎ¶¨ÏÖãÏóê ÎåÄÌïú ÏÑ§Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                value={formData.description}
                onChange={handleChange}
              />
            </div>
            
            {/* 2Îã®Í≥Ñ: ÎåÄÏÉÅ Í∑∏Î£π ÏÑ†ÌÉù (ÏÉòÌîåÍ≥º ÎèôÏùºÌïú ÎùºÎîîÏò§ Î≤ÑÌäº) */}
            <div className="form-group">
              <label>üéØ ÎåÄÏÉÅ Í∑∏Î£π ÏÑ†ÌÉù (ÌïÑÏàò)</label>
              <div className="radio-group" id="presetGroupList">
                {groups.length > 0 ? (
                  groups.map(group => (
                    <label 
                      key={group.id} 
                      className={`radio-label ${formData.group_id === group.id ? 'selected' : ''}`}
                    >
                      <input 
                        type="radio" 
                        name="presetGroup" 
                        value={group.id}
                        checked={formData.group_id === group.id}
                        onChange={() => handleGroupSelect(group.id)}
                      />
                      <span>
                        <strong>{group.name}</strong><br/>
                        <small style={{color: 'var(--text-muted)'}}>
                          {group.client_ids?.length || 0}Í∞ú ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏
                        </small>
                      </span>
                    </label>
                  ))
                ) : (
                  <div style={{textAlign: 'center', padding: '20px', color: 'var(--text-muted)'}}>
                    Î®ºÏ†Ä Í∑∏Î£πÏùÑ ÏÉùÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî
                  </div>
                )}
              </div>
              <small className="form-help">üìå Í∑∏Î£πÏùÑ ÏÑ†ÌÉùÌïòÎ©¥ Ìï¥Îãπ ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Î≥ÑÎ°ú Î™ÖÎ†πÏñ¥Î•º ÏÑ§Ï†ïÌï† Ïàò ÏûàÏäµÎãàÎã§</small>
            </div>
            
            {/* 3Îã®Í≥Ñ: ÏÑ†ÌÉùÎêú Í∑∏Î£πÏùò ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Î≥Ñ Î™ÖÎ†πÏñ¥ ÏÑ§Ï†ï */}
            {selectedGroup && (
              <div className="client-command-section" id="clientCommandSection">
                <div className="client-command-header">
                  <h4>üñ•Ô∏è ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Î≥Ñ Ïñ∏Î¶¨ÏñºÏóîÏßÑ Ïã§Ìñâ Î™ÖÎ†πÏñ¥</h4>
                  <span id="selectedGroupName" style={{color: 'var(--text-muted)', fontSize: '12px'}}>
                    ÏÑ†ÌÉùÎêú Í∑∏Î£π: {selectedGroup.name}
                  </span>
                </div>
                <div id="clientCommandsList">
                  {(selectedGroup.client_ids || []).map((clientId, index) => {
                    const client = clients.find(c => c.id === clientId);
                    if (!client) return null;
                    
                    return (
                      <div key={clientId} className="client-command-item">
                        <label htmlFor={`command_${clientId}`}>
                          üñ•Ô∏è {client.name} ({client.ip_address})
                        </label>
                        <input 
                          type="text" 
                          id={`command_${clientId}`}
                          data-client-id={clientId}
                          className="form-input" 
                          placeholder={`Ïòà: D:\\UnrealProjects\\MyProject\\Windows\\MyProject.exe -dc_node=Node_${index} -fullscreen`}
                          value={formData.client_commands[clientId] || ''}
                          onChange={(e) => handleCommandChange(clientId, e.target.value)}
                          required
                        />
                      </div>
                    );
                  })}
                </div>
                <small className="form-help">
                  üí° <strong>ÌåÅ:</strong> Í∞Å ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Î≥ÑÎ°ú Îã§Î•∏ nDisplay ÎÖ∏ÎìúÎ•º ÏÑ§Ï†ïÌïòÏÑ∏Ïöî<br/>
                  ÏòàÏãú: <code>D:\UnrealProjects\MyProject\Windows\MyProject.exe -dc_node=Node_0 -fullscreen</code>
                </small>
              </div>
            )}
            
            <div className="modal-actions">
              <button type="button" className="btn btn-secondary" onClick={onClose}>Ï∑®ÏÜå</button>
              <button type="submit" className="btn btn-primary">Ï†ÄÏû•</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PresetModal;
