import React from 'react';

const GroupSection = ({
  groups,
  clients,
  selectedGroups,
  onSelectAll,
  onBulkAction,
  onAddGroup,
  onEditGroup,
  onDeleteGroup,
  onSelectGroup
}) => {
  const allSelected = groups.length > 0 && selectedGroups.size === groups.length;

  return (
    <div className="section">
      <h2 className="section-title">
        ÎîîÏä§ÌîåÎ†àÏù¥ ÏÑúÎ≤Ñ Í∑∏Î£π
        <button className="btn btn-secondary btn-with-text" onClick={onAddGroup}>
          ‚ûï ÏÉà Í∑∏Î£π
        </button>
      </h2>
      {/* ÏùºÍ¥Ñ Ï†úÏñ¥ UI */}
      <div className="bulk-controls">
        <div className="selection-info">
          <label>
            <input
              type="checkbox"
              checked={allSelected}
              onChange={e => onSelectAll(e.target.checked)}
            />
            Ï†ÑÏ≤¥ ÏÑ†ÌÉù
          </label>
          <span>ÏÑ†ÌÉùÎêú Í∑∏Î£π: <span>{selectedGroups.size}</span>Í∞ú</span>
        </div>
        <div className="bulk-actions">
          <button className="btn btn-primary btn-bulk" onClick={() => onBulkAction('on')} title="ÏÑ†ÌÉùÎêú Í∑∏Î£πÎì§ Ï†ÑÏ≤¥ ÏºúÍ∏∞">
            üîå
          </button>
          <button className="btn btn-secondary btn-bulk" onClick={() => onBulkAction('reboot')} title="ÏÑ†ÌÉùÎêú Í∑∏Î£πÎì§ Ï†ÑÏ≤¥ Ïû¨Î∂ÄÌåÖ">
            üîÑ
          </button>
          <button className="btn btn-danger btn-bulk" onClick={() => onBulkAction('off')} title="ÏÑ†ÌÉùÎêú Í∑∏Î£πÎì§ Ï†ÑÏ≤¥ ÎÅÑÍ∏∞">
            ‚ö°
          </button>
        </div>
      </div>
      <div className="group-grid" id="groupGrid">
        {groups.length === 0 ? (
          <div style={{gridColumn: '1 / -1', textAlign: 'center', padding: 40, color: 'var(--text-muted)'}}>
            <div style={{fontSize: 48, marginBottom: 16}}>üë•</div>
            <div style={{fontSize: 14, marginBottom: 8}}>ÏïÑÏßÅ ÏÉùÏÑ±Îêú Í∑∏Î£πÏù¥ ÏóÜÏäµÎãàÎã§</div>
            <div style={{fontSize: 12}}>ÏÉà Í∑∏Î£π Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠Ìï¥ÏÑú Ï≤´ Î≤àÏß∏ Í∑∏Î£πÏùÑ ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî!</div>
          </div>
        ) : (
          groups.map(group => {
            const clientTags = (group.client_ids || []).map(clientId => {
              const client = clients.find(c => c.id === clientId);
              return client ? <span key={clientId} className="client-tag">{client.ip_address}</span> : null;
            });
            return (
              <div key={group.id} className="group-card">
                <input
                  type="checkbox"
                  className="group-checkbox"
                  data-group-id={group.id}
                  checked={selectedGroups.has(group.id)}
                  onChange={e => onSelectGroup(group.id, e.target.checked)}
                  onClick={e => e.stopPropagation()}
                />
                <div className="group-content">
                  <div className="group-name">{group.name}</div>
                  <div className="group-info">{(group.client_ids || []).length}Í∞ú ÎîîÏä§ÌîåÎ†àÏù¥ ÏÑúÎ≤Ñ</div>
                  <div className="group-clients">{clientTags}</div>
                </div>
                <div className="group-actions">
                  <button className="btn btn-secondary" onClick={() => onEditGroup(group.id)} title="Ìé∏Ïßë">‚úèÔ∏è</button>
                  <button className="btn btn-danger" onClick={() => onDeleteGroup(group.id)} title="ÏÇ≠Ï†ú">üóëÔ∏è</button>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default GroupSection;
