import React from 'react';

const PresetSection = ({
  presets,
  groups,
  selectedPresets,
  onSelectAll,
  onBulkAction,
  onAddPreset,
  onEditPreset,
  onDeletePreset,
  onExecutePreset,
  onSelectPreset
}) => {
  const allSelected = presets.length > 0 && selectedPresets.size === presets.length;

  return (
    <div className="section">
      <h2 className="section-title">
        üìã ÏΩòÌÖêÏ∏† ÌîÑÎ¶¨ÏÖã
        <button className="btn btn-secondary btn-with-text" onClick={onAddPreset}>
          ‚ûï ÏÉà ÌîÑÎ¶¨ÏÖã
        </button>
      </h2>
      {/* ÏùºÍ¥Ñ Ï†úÏñ¥ UI */}
      <div className="bulk-controls">
        <div className="selection-info">
          <label>
            <input
              type="checkbox"
              checked={allSelected}
              onChange={e => onSelectAll(e.target.checked)}
            />
            Ï†ÑÏ≤¥ ÏÑ†ÌÉù
          </label>
          <span>ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖã: <span>{selectedPresets.size}</span>Í∞ú</span>
        </div>
        <div className="bulk-actions">
          <button className="btn btn-primary btn-bulk" onClick={() => onBulkAction('execute')} title="ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖãÎì§ Ï†ÑÏ≤¥ Ïã§Ìñâ">
            ‚ñ∂Ô∏è
          </button>
          <button className="btn btn-danger btn-bulk" onClick={() => onBulkAction('stop')} title="ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖãÎì§ Ï†ÑÏ≤¥ Ï†ïÏßÄ">
            ‚èπÔ∏è
          </button>
          <button className="btn btn-danger btn-bulk" onClick={() => onBulkAction('delete')} title="ÏÑ†ÌÉùÎêú ÌîÑÎ¶¨ÏÖãÎì§ Ï†ÑÏ≤¥ ÏÇ≠Ï†ú">
            üóëÔ∏è
          </button>
        </div>
      </div>
      <div className="preset-grid" id="presetGrid">
        {presets.length === 0 ? (
          <div style={{gridColumn: '1 / -1', textAlign: 'center', padding: 40, color: 'var(--text-muted)'}}>
            <div style={{fontSize: 48, marginBottom: 16}}>üìù</div>
            <div style={{fontSize: 14, marginBottom: 8}}>ÏïÑÏßÅ ÏÉùÏÑ±Îêú ÌîÑÎ¶¨ÏÖãÏù¥ ÏóÜÏäµÎãàÎã§</div>
            <div style={{fontSize: 12}}>ÏÉà ÌîÑÎ¶¨ÏÖã Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠Ìï¥ÏÑú Ï≤´ Î≤àÏß∏ ÌîÑÎ¶¨ÏÖãÏùÑ ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî!</div>
          </div>
        ) : (
          presets.map(preset => {
            // ÏÉàÎ°úÏö¥ Íµ¨Ï°∞Ïóê ÎßûÍ≤å Í∑∏Î£π Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
            const group = groups.find(g => g.id === preset.group_id);
            const groupName = group ? group.name : 'ÏÇ≠Ï†úÎêú Í∑∏Î£π';
            const clientCount = preset.client_commands ? Object.keys(preset.client_commands).length : 0;
            
            return (
              <div key={preset.id} className={`preset-card${preset.is_active ? ' active' : ''}`} id={`preset-${preset.id}`}>
                <input
                  type="checkbox"
                  className="preset-checkbox"
                  data-preset-id={preset.id}
                  checked={selectedPresets.has(preset.id)}
                  onChange={e => onSelectPreset(preset.id, e.target.checked)}
                  onClick={e => e.stopPropagation()}
                />
                <div className="preset-content">
                  <div className="preset-name">{preset.name}</div>
                  {preset.description && <div className="preset-info">{preset.description}</div>}
                  <div className="preset-info">Í∑∏Î£π: {groupName}</div>
                  <div className="preset-info">{clientCount}ÎåÄ ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏</div>
                </div>
                <div className="preset-actions">
                  <button className="btn btn-primary" onClick={() => onExecutePreset(preset.id)} title="Ïã§Ìñâ">‚ñ∂Ô∏è</button>
                  <button className="btn btn-secondary" onClick={() => onEditPreset(preset.id)} title="Ìé∏Ïßë">‚úèÔ∏è</button>
                  <button className="btn btn-danger" onClick={() => onDeletePreset(preset.id)} title="ÏÇ≠Ï†ú">üóëÔ∏è</button>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default PresetSection;
